import React, { Component } from 'react';
import './App.css';
import Markdown from 'markdown-to-jsx';
import resume from './resume.md';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = { md: '' };
  }

  componentWillMount() {
    fetch(resume)
      .then((res) => res.text())
      .then((md) => {
        this.setState({ md })
      })
  }

  render() {
    let { md } = this.state;

    return (
      <div className="App">
        <div className="App-content">
          <Markdown children={md} />
        </div>
      </div>
    );
  }
}

export default App;
